def hex_conv(x):
    return hex(x).lstrip("0x").rstrip("L")
def hex_deconv(x):
    x = int(x, base=16)
    return x

def encryption(pk, m, pp):
    p, g = pp
    list_m = []
    C = ""
    for x in m:
        list_m.append(x)

def decryption(sk, C, pp, mapping):
    p, g = pp
    list_C = list(filter(None, C.split(";")))
    list_m = ""
    for mc in list_C:
        list_mc = list(filter(None, mc.split(":")))
        c1 = hex_deconv(list_mc[0])
        c2 = hex_deconv(list_mc[1])
        ppp = pow(c2, -sk, p)
        m = (c1 * ppp)% p
        m = mapping[m]
        list_m = list_m + chr(m)
    return list_m


